import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useFetching } from '../hook/useFetching';
import PostService from '../API/PostService';

const PostIdPage = () => {
    const params = useParams()  //хук позволяет закрать с url параметр (номер поста)
    const [fetchPostId, isPostIdLoading, postIdError] = useFetching(() => {
        const responce = PostService.getById()
        console.log(responce);
    }) 

    useEffect(() => {
        fetchPostId()
    }, [])

    return (
        <div>
            <h1>Вы октрыли страницу поста {params.id}</h1>
        </div>
    );
};

export default PostIdPage;
