import React, { useState } from 'react';
import MyInput from './UI/input/MyInput';
import MyTextarea from './UI/textarea/MyTextarea';
import MyButton from './UI/button/MyButton';

const FormContact = () => {

    const [name, setName] = useState('') 
    const [nameDirty, setNameDirty] = useState('false')
    const [nameError, setNameError] = useState('Имя не может быть пустым')
    const [email, emailName] = useState('') 
    const [emailDirty, setEmailDirty] = useState('false')
    const [emailError, setEmailError] = useState('email не может быть пустым')
    const [comment, setComment] = useState('') 
    const [commentDirty, setCommentDirty] = useState('false')
    const [commentError, setCommentError] = useState('comment не может быть пустым')





    return (
        <form className='form_contact'>
            {(nameDirty && nameError) && <div style={{color: "red"}}>{nameError}</div>}
            <MyInput value={name} onChange = {(e)=> setName(e.target.value)} name="name" type="text" placeholder="Имя" />
            <MyInput value={email} onChange = {(e)=> emailName(e.target.value)} name="email" type="email" placeholder="Email" />
            <MyTextarea value={comment} onChange = {(e)=> setComment(e.target.value)} name="comment" type="text" placeholder="Комментарий" />
            <div className='flex flex_end'>
                <MyButton>Отправить</MyButton>
            </div>
        </form>
    );
};

export default FormContact;
