import React, { useState } from 'react';
import MyInput from './UI/input/MyInput';
import MyTextarea from './UI/textarea/MyTextarea';
import MyButton from './UI/button/MyButton';

const FormContact = () => {

    const [name, setName] = useState('') 
    const [nameDirty, setNameDirty] = useState(false)
    const [nameError, setNameError] = useState('Имя не может быть пустым')
    const [email, setEmail] = useState('') 
    const [emailDirty, setEmailDirty] = useState(false)
    const [emailError, setEmailError] = useState('email не может быть пустым')
    const [comment, setComment] = useState('') 


    const emailHandler = (e) => {
        setEmail(e.target.value)
        const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if(!re.test(String(e.target.value).toLowerCase())){
            setEmailError('Некоректный email')
        }
    }





   //При смене фокуса с поля инпут 
    const blurHandler = (e) => {
        switch(e.target.name){
            case 'name': 
            setNameDirty(true)
            break

            case 'email': 
            setEmailDirty(true)
            break
        }
    }




    return (
        <form className='form_contact'>
            {(nameDirty && nameError) && <div style={{color: "red"}}>{nameError}</div>}
            <MyInput onBlur={e => blurHandler(e)} value={name}  name="name" type="text" placeholder="Имя" />
            {(emailDirty && emailError) && <div style={{color: "red"}}>{emailError}</div>}
            <MyInput onBlur={e => blurHandler(e)} value={email}  name="email" type="email" placeholder="Email" />
            <MyTextarea value={comment} onChange = {(e)=> setComment(e.target.value)} name="comment" type="text" placeholder="Комментарий" />
            <div className='flex flex_end'>
                <MyButton type="submit">Отправить</MyButton>
            </div>
        </form>
    );
};

export default FormContact;
