import React, { useEffect, useState } from 'react';
import PostList from '../components/PostList';
import PostForm from '../components/PostForm';
import PostFilter from '../components/PostFilter';
import MyModal from '../components/UI/modal/MyModal';
import MyButton from '../components/UI/button/MyButton';
import { usePosts } from '../components/hook/usePost';
import PostService from '../components/API/PostService';
import Loader from '../components/UI/loader/Loader';
import { useFetching } from '../components/hook/useFetching';



const Posts = () => {

    const [posts, setPosts] = useState([]);
    const [filter, setFilter] = useState({sort: '', query: '' })
    const [modal, setModal] = useState(false)
    const sortedAndSearchedPosts = usePosts(posts, filter.sort, filter.query)
    const[fetchPosts, isPostsLoading, postError]= useFetching(async() => {
      const posts = await PostService.getAll();
      setPosts(posts)
    })

    //хук для управления жизненным циклом компонента (получение данных при первичной отрисовке)
    useEffect(() => {
      fetchPosts()
    }, [])



    //добавить новый пост
    const createNewPost = (newPost) => {
      setPosts([...posts, newPost])
      setModal(false)
    }


    //удалить пост
    const removePost = (post) => {
      setPosts(posts.filter(p=> p.id !== post.id))
    }


    return (
        <div>
       <MyModal visible={modal} setVisible={setModal} >
         <PostForm create={createNewPost}/>
       </MyModal>


      <PostFilter filter={filter}  setFilter={setFilter}/>

      <MyButton style={{marginTop: 15}} onClick={() => setModal(true)}>
        Создать пост
      </MyButton>
        { postError &&
            <div style={{display: 'flex', justifyContent: 'center', marginTop:30}}><h2>Произошла ошибка ${postError}</h2></div>
        }
        { isPostsLoading 
          ? <div style={{display: 'flex', justifyContent: 'center', marginTop:30}}><Loader/></div>
          : <PostList remove={removePost}  posts={sortedAndSearchedPosts} title={'Список постов'}/>
        }

    </div>
    );
};

export default Posts;
